/*
 查找Lua元素的路由 by jc 2017/4/14
 */

const LUA_METHOD_MAP = {
    // 查找元素                     完成
    '/wd/hub/session/:sessionId/lua/element': {
        POST: {command: 'findLuaElement', payloadParams: {required: ['using', 'value']}}
    },

    //                              完成
    '/wd/hub/session/:sessionId/lua/elements': {
        POST: {command: 'findLuaElements', payloadParams: {required: ['using', 'value']}}
    },

    // 设置文本                     完成
    '/wd/hub/session/:sessionId/lua/element/:elementId/value': {
        POST: {command: 'setLuaValue', payloadParams: {required: ['value']}}
    },

    // 点击元素                     完成
    '/wd/hub/session/:sessionId/lua/element/:elementId/click': {
        POST: {command: 'luaClick'}
    },

    // 得到元素属性(文本: text)     完成
    '/wd/hub/session/:sessionId/lua/element/:elementId/attribute/:name': {
        GET: {command: 'getLuaAttribute'}
    },

    // 获取文本 （是否需要？貌似是针对web的
    '/wd/hub/session/:sessionId?/lua/element/:elementId?/text': {
        GET: {command: 'getLuaText'}
    },

    //  获取元素 displayed 属性值      完成
    '/wd/hub/session/:sessionId/lua/element/:elementId/displayed': {
        GET: {command: 'luaElementDisplayed'}
    },

    // 获取元素 enabled 属性值         完成
    '/wd/hub/session/:sessionId/lua/element/:elementId/enabled': {
        GET: {command: 'luaElementEnabled'}
    },

    // 获取元素 selected 属性值        完成
    '/wd/hub/session/:sessionId/lua/element/:elementId/selected': {
        GET: {command: 'luaElementSelected'}
    },

    // 获取元素 location               完成
    '/wd/hub/session/:sessionId/lua/element/:elementId/location': {
        GET: {command: 'getLuaLocation'}
    },

    // 本质是调用getLuaLocation          完成
    '/wd/hub/session/:sessionId/lua/element/:elementId/location_in_view': {
        GET: {command: 'getLuaLocationInView'}
    },

    // 获取元素 size                    完成
    '/wd/hub/session/:sessionId/lua/element/:elementId/size': {
        GET: {command: 'getLuaSize'}
    },

    // 跑adb命令
    '/wd/hub/session/:sessionId/adb': {
        POST: {command: 'adbCommand', payloadParams: {required: ['params']}}
    }
}
export default LUA_METHOD_MAP